import React, { Component } from 'react'
import axios from 'axios'
export default class Right extends Component {
    constructor(){
        super();
        this.state={
            prodata:[]
        }
    }
    componentDidMount(){
        axios
        .get('https://fakestoreapi.com/products')
        .then(res=>{
            console.log(res.data);
            this.setState({
                prodata:res.data
            })
        })
    }
    componentDidUpdate(prevProps){
      console.log('DID UPDATE',this.props.abc);
      console.log(this.props.abc,prevProps);
      if(this.props.abc!="" && (this.props.abc!=prevProps.abc)){
        axios
        .get(`https://fakestoreapi.com/products/category/${this.props.abc}`)
      }
    }
  render() {
    const API = this.state.prodata;

    return (
      <div>
      <h1>All Products</h1>
      <div className='row'>
      {
        API && API.length>0 && API.map(val=>
            <div className='col-md-3 text center'>
            <img src={val.image} alt="" className='img-responsive'/>
            <h2>{val.price}</h2>
            <p>{val.title}</p>
            <p>{val.category}</p>
            </div>
            )
      }
      </div>
      </div>
    )
  }
}
