import React, { Component } from 'react'
import MainContext from '../ContextApi'
MainContext

export default class Child3 extends Component {
  render() {
    return (
      <div>Child3 , From Main {this.props.pqr}
      <br />
      <h2>Data From Provide</h2>
      <MainContext.Consumer>
      {
        value=>(
            <h1>{value}</h1>
        )
      }
      </MainContext.Consumer>
      </div>
    )
  }
}
